     <style>
      .loader{
                width: 100%;
              height: 100%;
              top: 0;
              left: 0;
              position: fixed;
              background-color: rgba(0,0,0,0.7);
              display: flex;
              justify-content: center;
              align-items: center;
              z-index: 10000 !important;
              }
          .water{
              width:400px;
              height: 400px;
              background-color: skyblue;
              border-radius: 50%;
              position: relative;
              box-shadow: inset 0 0 30px 0 rgba(0,0,0,.5), 0 4px 10px 0 rgba(0,0,0,.5);
              overflow: hidden;
          }
          .water:before, .water:after{
              content:'';
              position: absolute;
              width:400px;
              height: 400px;
              top:0px;
              background-color: #fff;
          }
          .water:before{
              border-radius: 45%;
              background:rgba(255,255,255,.7);
              animation:wave 5s linear infinite;
          }
          .water:after{
              border-radius: 35%;
              background:rgba(255,255,255,.3);
              animation:wave 5s linear infinite;
          }
          .loader p{
            font-size: 20px;
          }
          .loader #p01{
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%,-50%);
            z-index: 10001;
            animation:loading1 1.2s infinite;
          }
          .loader #p02{
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%,-50%);
            z-index: 10001;
            animation:loading2 1.2s infinite;
          }
          .loader #p03{
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%,-50%);
            z-index: 10001;
            animation:loading3 1.2s infinite;
          }
          @keyframes loading1{
            0%{
              opacity: 1;
            }
            50%{
              opacity: 0;
            }
            100%{
              opacity: 0;
            }
          }
          @keyframes loading2{
            0%{
              opacity: 0;
            }
            50%{
              opacity: 1;
            }
            100%{
              opacity: 0;
            }
          }
          @keyframes loading3{
            0%{
              opacity: 0;
            }
            50%{
              opacity: 0;
            }
            100%{
              opacity: 1;
            }
          }
          @keyframes wave{
              0%{
                  transform: rotate(0);
                  top:0;
                  
              }
              100%{
                  transform: rotate(360deg);
                  top:-400px;
                }
          }
          @media (max-width: 500px){
            .water{
              width: 300px;
              height: 300px;
              box-shadow: inset 0 0 30px 0 rgba(0,0,0,.5), 0 4px 10px 0 rgba(0,0,0,.5);
              overflow: hidden !important;
            }
            @keyframes wave{
              0%{
                  transform: rotate(0);
                  top:0;
                  
              }
              100%{
                  transform: rotate(360deg);
                  top:-400px;
                }
          }
          }
         
        </style>
